import React, { Component } from 'react';
import axios from 'axios';

export default class GenreForm extends Component {
    constructor(props) {
        super(props);

        this.onChangeGenre = this.onChangeGenre.bind(this);
        this.onSubmit = this.onSubmit.bind(this);

        this.state = {
            genre: ''
        }

    }

    api() {
      const spotify = Credentials();  

      console.log('RENDERING APP.JS');

      const [token, setToken] = useState(''); 
      const [filter, setFilter] = useState('');
      const [filterToSend, setFilterToSend] = useState('')
      const [items, setItems] = useState([]);
      console.log('token', token)
      console.log('filter', filter)
      console.log('items', items)
      //when the app loads, request a token from spotify
      //and save it to state.



      useEffect(async() => {
        const response = await axios('https://accounts.spotify.com/api/token', {
            headers: {
            'Content-Type' : 'application/x-www-form-urlencoded',
            'Authorization' : 'Basic ' + btoa(spotify.ClientId + ':' + spotify.ClientSecret)      
          },
          data: 'grant_type=client_credentials',
          method: 'POST'
        })
        setToken(response.data.access_token)
        //empty array dependency = this runs once on load
        },[])



      const handleSearch = async (e) => {
        //preventDefault keeps page from rerendering and bad things happening
        e.preventDefault()
        console.log('filter', filter)
        //call axios with filter and token from state
        const response = await axios(`https://api.spotify.com/v1/search?q=genre:"${filter}"&type=artist`, {
          method: 'GET', 
          headers: { 'Authorization' : 'Bearer ' + token}
        })
        //set response into state
        setItems(response.data.artists.items)
        }
    }

    onChangeGenre(e) {
        this.setState({
            genre: e.target.value
        })
    }

    onSubmit(e) {
        e.preventDefault();

        const savedGenre = {
            genre: this.state.genre
        }

        console.log(savedGenre);

        axios.post('http://localhost:3001/genres/', savedGenre)
        .then(res => console.log(res.data));

        this.setState({
            genre: ''
        })
    }

    render() {
        return (
          <div>
            <h3>pick a genre</h3>
            <form onSubmit={this.onSubmit}>
              <div className="form-group"> 
                <label>genre:</label>
                <input type="text"
                    required
                    className="form-control-md"
                    value={this.state.genre}
                    onChange={this.onChangeGenre}
                    />
              </div>
              <div className="form-group">
                <input type="submit" value="PICK" className="btn btn-primary" />
              </div>
            </form>
          </div>
        )
      }


}
