  //     setToken(Response.data.spotify)
  //   })
  // })


  return (
    <div className="App">
      <h1>genrerator</h1>
      <Dropdown options={data} />
    <form>
      <input type="text" name="artist name" onClick={(e) => setFilter(e.target.value)} />
      <input type="submit" value="Submit" onClick={handleSearch}/>
    </form>

    </div>
  );
}

export default App;
