import { useEffect, useState } from 'react';
import './App.css';
import Dropdown from './Dropdown';
import axios from 'axios';
import { Credentials } from './Credentials'

const App = () => {

  const spotify = Credentials();  

  console.log('RENDERING APP.JS');

  const data = [
    {value: 1, name:"pop"},
    {value: 2, name:"dance pop"},
    {value: 3, name:"pop rap"},
  ];

  const [token, setToken] = useState(''); 
  const [search, setSearch] = useState({searchQuery: '', listOfQuery: []}) 

  useEffect(() => {
    axios('https://accounts.spotify.com/api/token', {
      headers: {
        'Content-Type' : 'application/x-www-form-urlencoded',
        'Authorization' : 'Basic ' + btoa(spotify.ClientId + ':' + spotify.ClientSecret)      
      },
      data: 'grant_type=client_credentials',
      method: 'POST'
    })
    .then(tokenResponse => {
      setToken(tokenResponse.data.access_token);

    axios('https://api.spotify.com/v1/browse/categories?locale=sv_US', {
      method: 'GET', 
      headers: { 'Authorization' : 'Bearer ' + tokenResponse.data.access_token}
    })
    .then (searchResponse => {
      setSearch({
        searchQuery: search.searchQuery, 
        listOfQuery: searchResponse.data.categories.items
      })
    })
    })
  }, [search.searchQuery, spotify.ClientId, spotify.ClientSecret]);


  return (
    <div className="App">
      <h1>genrerator</h1>
      <Dropdown options={data} />
    </div>
  );
}

export default App;
