    // }

  return (
    <Router>
    <div className="App" >
      <h1>genrerator</h1>
      <form>
        <p>search for a genre of music you enjoy</p>
        <input type="text" name="genre" onChange={(e) => setFilter(e.target.value)} />
        <br></br>
        <p>or pick one here</p>
        <select value="this.setState" onChange={(e) => setFilter(e.target.value)}>
          <option value="genre">choose one!</option>
          <option value="nz indie">nz indie</option>
