import { useCallback, useEffect, useState } from 'react';
import './App.css';
import Dropdown from './Dropdown';
import axios from 'axios';
import { Credentials } from './Credentials'

const App = () => {

  const spotify = Credentials();  

  console.log('RENDERING APP.JS');

  const data = [
    {value: 1, name:"pop"},
    {value: 2, name:"dance pop"},
    {value: 3, name:"pop rap"},
  ];

  const [token, setToken] = useState(''); 
  const [filter, setFilter] = useState('');
  const [filterToSend, setFilterToSend] = useState('')
  const [items, setItems] = useState([]);
  console.log('token', token)
  console.log('filter', filter)
  console.log('items', items)
  //when the app loads, request a token from spotify
  //and save it to state.



  useEffect(async() => {
    const response = await axios('https://accounts.spotify.com/api/token', {
        headers: {
        'Content-Type' : 'application/x-www-form-urlencoded',
        'Authorization' : 'Basic ' + btoa(spotify.ClientId + ':' + spotify.ClientSecret)      
      },
      data: 'grant_type=client_credentials',
      method: 'POST'
    })
    setToken(response.data.access_token)
    //empty array dependency = this runs once on load
  },[])

  const handleSearch = async (e) => {
    //preventDefault keeps page from rerendering and bad things happening
    e.preventDefault()
    console.log('filter', filter)
    //call axios with filter and token from state
    const response = await axios(`https://api.spotify.com/v1/search?q=${filter}&type=artist`, {
      method: 'GET', 
      headers: { 'Authorization' : 'Bearer ' + token}
    })
    //set response into state
    setItems(response.data.artists.items)
    }

  return (
    <div className="App">
      <h1>genrerator</h1>
      <Dropdown options={data} />
    <form>
      <input type="text" onChange={(e) => setFilter(e.target.value)} />
      <input type="submit" value="Submit" onClick={(e) => handleSearch(e)} />
    </form>
    <div>
      {items.map(item => 
      <div>
        {item.name}
      </div>)}
    </div>

    </div>
  );
}

export default App;
